        data: [1234, 2030, 1580, 788, 1700, 1150, 1384],
        type: 'bar',
        itemStyle: {
          color: '#3296FA',
        },
      },
    ],
  },
  hot: {
    tooltip: {
      trigger: 'item',
    },
    legend: {
      top: '95%',
      left: 'center',
    },
    series: [
      {
        name: '品类热度',
        type: 'pie',
        radius: ['40%', '70%'],
        avoidLabelOverlap: false,
        itemStyle: {
          borderRadius: 10,
          borderColor: '#fff',
          borderWidth: 2,
        },
        label: {
          show: false,
          position: 'center',
        },
        emphasis: {
          label: {
            show: true,
            fontSize: 20,
            fontWeight: 'bold',
          },
        },
        labelLine: {
          show: false,
        },
        data: [
          { value: 1048, name: '数码', itemStyle: { color: '#1296db' } },
          { value: 735, name: '母婴', itemStyle: { color: '#3296FA' } },
          { value: 580, name: '食品', itemStyle: { color: '#00B42A' } },
          { value: 484, name: '运动', itemStyle: { color: '#FF7D00' } },
          { value: 300, name: '家电', itemStyle: { color: '#F53F3F' } },
        ],
      },
    ],
  },
}

// 初始化图表
const initCharts = () => {
  // 订单量图表
  if (orderCard.value) {
    myChart = echarts.init(orderCard.value, null, { renderer: 'canvas' })
    myChart.setOption(chartOptions.order)
  }

  // 销售额图表
  if (salesCard.value) {
    salesChart = echarts.init(salesCard.value, null, { renderer: 'canvas' })
    salesChart.setOption(chartOptions.sales)
  }

  // 品类热度图表
  if (hotCard.value) {
    hotChart = echarts.init(hotCard.value, null, { renderer: 'canvas' })
    hotChart.setOption(chartOptions.hot)
  }
}

// 调整图表大小（使用防抖优化性能）
const handleResize = debounce(() => {
  myChart?.resize()
  salesChart?.resize()
  hotChart?.resize()
}, 150)

onMounted(() => {
  // 等待DOM渲染完成后初始化图表
  nextTick(() => {
    initCharts()
    // 监听窗口大小变化
    window.addEventListener('resize', handleResize)
  })
})

onUnmounted(() => {
  // 移除事件监听并销毁图表实例
  window.removeEventListener('resize', handleResize)
  myChart?.dispose()
  salesChart?.dispose()
  hotChart?.dispose()
})
</script>

